SELECT ind_bills_master.EMPID, EMPNAME(ind_bills_master.EMPID),empbillsectid(ind_bills_master.empid),paybill.amount, emp_master.prannumber from emp_master, bills_det, paybill, ind_bills_master where ind_bills_master.empid=emp_master.empid and bills_det.ind_billno = ind_bills_master.ind_bill_no and paybill.ind_bill_no = ind_bills_master.ind_bill_no and btype ='SAL' and paybill.indid='NPS' and bills_det.billno between '2021001481' and '2021001551'