select ind_bills_master.empid,  empname(ind_bills_master.empid), sum(paybill.amount) as EmployeeContribution, sum(paybill.amount) as EmployerContribution, sum(paybill.amount*2) as Total from emp_master,ind_bills_master, paybill, bills_det where emp_master.empid = ind_bills_master.empid and paybill.ind_bill_no = ind_bills_master.ind_bill_no and bills_det.ind_billno = paybill.ind_bill_no and ind_bills_master.ind_bill_no= bills_det.ind_billno and paybill.indid='NPS' and billno between '2021001552' and '2021001781'  and btype ='INC' group by ind_bills_master.empid