SELECT ind_bills_master.empid, empname(ind_bills_master.empid),sli_trans.policyno, sli.premiumamt, paybill.amount from sli, sli_trans, paybill, ind_bills_master where sli.empid= ind_bills_master.empid and sli.policyno = sli_trans.policyno and sli_trans.ind_bill_no = ind_bills_master.ind_bill_no and ind_bills_master.ind_bill_no = paybill.ind_bill_no and btype ='SAL' and paybill.indid ='SLI' and ofdate ='01/02/2022' and sli_trans.policyno like 'MGSLI2022/%' order by empid