select distinct ind_bills_master.empid, empname(ind_bills_master.empid), empoffice(ind_bills_master.empid),emp_master.doret from emp_master, ind_bills_master where ind_bills_master.ofdate between '01/08/2019' and '01/03/2021' and ind_bills_master.empid = emp_master.empid and  ind_bills_master.btype='SAL' order by empid